import React, { useState } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleEmailLogin = (e: React.FormEvent) => {
    e.preventDefault();
    // Fake login for now – real backend later
    login({ id: '1', name: 'John Doe', email, role: 'developer' });
    navigate('/dashboard');
  };

  const handleGoogleSuccess = (response: any) => {
    console.log('Google Login Success:', response);
    login({ id: '2', name: 'Google User', email: 'google@example.com', role: 'client' });
    navigate('/dashboard');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 to-purple-900 flex items-center justify-center px-6">
      <div className="bg-white rounded-3xl shadow-2xl p-10 w-full max-w-md">
        <h1 className="text-4xl font-bold text-center mb-8 text-gray-800">Welcome Back</h1>
        
        <form onSubmit={handleEmailLogin} className="space-y-6">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full px-6 py-4 border border-gray-300 rounded-full text-lg focus:outline-none focus:border-blue-500"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-6 py-4 border border-gray-300 rounded-full text-lg focus:outline-none focus:border-blue-500"
            required
          />
          <button className="w-full bg-blue-600 text-white py-4 rounded-full text-xl font-bold hover:bg-blue-700 transition">
            Login
          </button>
        </form>

        <div className="my-8 text-center text-gray-500">OR</div>

        <div className="flex justify-center">
          <GoogleLogin
            onSuccess={handleGoogleSuccess}
            onError={() => console.log('Login Failed')}
            useOneTap
          />
        </div>

        <p className="text-center mt-8 text-gray-600">
          Don't have an account? <a href="/register" className="text-blue-600 font-bold">Sign up</a>
        </p>
      </div>
    </div>
  );
}
