import React from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

export default function Dashboard() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-gray-100 p-10">
      <div className="max-w-4xl mx-auto bg-white rounded-3xl shadow-xl p-10">
        <h1 className="text-5xl font-bold mb-6">Welcome back, {user?.name}!</h1>
        <p className="text-2xl text-gray-600 mb-8">Role: <span className="font-bold text-blue-600">{user?.role.toUpperCase()}</span></p>
        
        <button
          onClick={() => { logout(); navigate('/'); }}
          className="bg-red-600 text-white px-8 py-4 rounded-full text-xl font-bold hover:bg-red-700"
        >
          Logout
        </button>
      </div>
    </div>
  );
}
