// src/pages/Login.jsx
import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import useAuth from '../contexts/useAuth';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

 // In both Login.jsx and Register.jsx — replace handleSubmit with this:
const handleSubmit = (e) => {
  e.preventDefault();
  // Replace with your real API later
  localStorage.setItem('authToken', 'real-jwt-token-here');
  localStorage.setItem('userName', 'John Doe'); // optional
  window.location.href = '/dashboard'; // instant redirect
};
  return (
    <div className="max-w-md mx-auto mt-20 p-8 bg-white rounded-xl shadow-xl">
      <h2 className="text-3xl font-bold mb-6 text-center">Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="w-full px-4 py-3 mb-4 border rounded-full"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full px-4 py-3 mb-6 border rounded-full"
        />
        <button type="submit" className="w-full bg-blue-600 text-white py-3 rounded-full font-bold">
          Login
        </button>
      </form>
      <p className="mt-4 text-center">
        No account? <Link to="/register" className="text-blue-600">Sign Up</Link>
      </p>
    </div>
  );
};

export default Login;
