import { Link, useNavigate } from 'react-router-dom';

export default function Navbar() {
  const navigate = useNavigate();
  const user = localStorage.getItem('user'); // or however you store login state

  return (
    <nav className="bg-blue-700 text-white p-4 shadow-lg">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold">DevSync</Link>
        
        <div className="space-x-8">
          <Link to="/" className="hover:underline">Home</Link>
          <Link to="/services" className="hover:underline">Services</Link>
          <Link to="/about" className="hover:underline">About</Link>
          <Link to="/contact" className="hover:underline">Contact</Link>
          
          {user ? (
            <Link to="/dashboard" className="bg-white text-blue-700 px-4 py-2 rounded-lg font-bold">
              Dashboard
            </Link>
          ) : (
            <>
              <Link to="/login" className="hover:underline">Login</Link>
              <Link to="/register" className="bg-white text-blue-700 px-4 py-2 rounded-lg font-bold">
                Sign Up
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}
